import React, { useContext, useState } from 'react'
import { Context } from '../context/GlobalContextProvider'
import { Form } from '../component'
import categoriesData from '../utils/categoriesData'

type idType = {
  id: string
}

function FormContainer() {
  const {
    nameToReplace,
    category,
    handleSubmit,
    setNameToReplace,
    setCategory,
    jokesData,
  } = useContext(Context)

  let buttonLabelName: string

  if (nameToReplace) {
    buttonLabelName = nameToReplace
  } else if (jokesData.name) {
    buttonLabelName = jokesData.name
  } else {
    buttonLabelName = 'Chuck Norris'
  }

  const data = [
    { id: 0, name: 'Istanbul, TR (AHL)' },
    { id: 1, name: 'Paris, FR (CDG)' },
  ]

  const [isOpen, setOpen] = useState(false)
  const [items, setItem] = useState(data)
  const [selectedItem, setSelectedItem] = useState(null)

  const toggleDropdown = () => setOpen(!isOpen)

  const handleItemClick = (id: any) => {
    selectedItem == id ? setSelectedItem(null) : setSelectedItem(id)
  }

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Dropdown className='dropdown'>
        <Form.DropdownHeader
          className='dropdown-header'
          onClick={toggleDropdown}>
          {selectedItem
            ? items.find((item) => item.id == selectedItem).name
            : 'Select your destination'}
          <i className={`fa fa-chevron-right icon ${isOpen && 'open'}`}></i>
        </Form.DropdownHeader>
        <Form.DropdownBody className={`dropdown-body ${isOpen && 'open'}`}>
          {items.map((item) => (
            <Form.DropdownItem
              className='dropdown-item'
              onClick={(e) => handleItemClick((e.target as Element).id)}
              id={item.id}
              key={item.id}>
              <Form.DropdownItemDot
                className={`dropdown-item-dot ${
                  item.id == selectedItem && 'selected'
                }`}>
                â€¢{' '}
              </Form.DropdownItemDot>
              {item.name}
            </Form.DropdownItem>
          ))}
        </Form.DropdownBody>
      </Form.Dropdown>

      <Form.Fieldset>
        <Form.Input
          type='text'
          value={nameToReplace}
          placeholder='Impersonate Chuck Norris'
          onChange={setNameToReplace}
        />
      </Form.Fieldset>
      <Form.Button type='submit'>
        Draw a random {buttonLabelName} Joke
      </Form.Button>
    </Form>
  )
}

export default FormContainer
