import React, { useContext, useState } from 'react'
import { Context } from '../context/GlobalContextProvider'
import { Form } from '../component'

function FormContainer() {
  const {
    nameToReplace,
    category,
    handleSubmit,
    searchCountryByName,
    selectCountryByRegion,
    jokesData,
  } = useContext(Context)

  let button

  if (nameToReplace) {
    button = nameToReplace
  } else if (jokesData.name) {
    button = jokesData.name
  } else {
    button = 'Chuck Norris'
  }

  return (
    <div className='App'>
      <Form onSubmit={handleSubmit}>
        <Form.Fieldset>
          <Form.Select
            name='category'
            placeholder='Categories'
            value={category}
            onChange={selectCountryByRegion}>
            <option value=''></option>
            <option value='nerdy'>nerdy</option>
            <option value='explicit'>explicit</option>
          </Form.Select>
        </Form.Fieldset>
        <Form.Fieldset>
          <Form.Input
            value={nameToReplace}
            placeholder='Impersonate Chuck Norris'
            onChange={searchCountryByName}
            name='firstName'
          />
        </Form.Fieldset>
        <Form.Button type='submit'>
          Draw a random {button}
          Joke
        </Form.Button>
      </Form>
    </div>
  )
}

export default FormContainer
