import React, { useReducer, useEffect, createContext, useState } from 'react'
import { State } from '../utils/Types'
import reducer from './Reducer'
import { RANDOM_API, LIMIT_TO } from '../constants'

const endpointR = 'http://api.icndb.com/jokes'

const initialValue: State = {
  loading: true,
  jokes: [],
  nameToReplace: '',
  category: '',
  jokesData: {},
  selectCountryByRegion: () => {},
  // setJokesData: () => {},
  searchCountryByName: () => {},
  handleSubmit: () => {},
}

export const Context = createContext(initialValue)

export const GlobalContext: React.FC = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialValue)
  const [jokesData, setJokesData] = useState({
    name: '',
    category: '',
  })

  const fetchRandomJokes = async () => {
    const countryData = await fetch(RANDOM_API)
    const countryRes = await countryData.json()
    dispatch({ type: 'FETCH_RANDOM_JOKES', payload: countryRes.value })
  }

  const fetchJokesWithCategory = async () => {
    const countryData = await fetch(endpointR)
    const countryRes = await countryData.json()
    const filteredJokeWithCat = countryRes?.value.filter(
      (j: any) => j.categories[0] === state.category
    )
    const randomiseFilteredJoke =
      filteredJokeWithCat[
        Math.floor(Math.random() * filteredJokeWithCat.length)
      ]
    dispatch({
      type: 'FETCH_JOKES_WITH_CATEGORY',
      payload: randomiseFilteredJoke,
    })
  }

  function handleSubmit(e: React.FormEvent<HTMLFormElement>) {
    e.preventDefault()
    const nameObj = {
      name: state.nameToReplace,
      category: state.category,
    }
    setJokesData(nameObj)
  }

  useEffect(() => {
    if (jokesData.category !== '') {
      fetchJokesWithCategory()
    } else {
      fetchRandomJokes()
    }
  }, [jokesData])

  return (
    <Context.Provider
      value={{
        jokes: state.jokes,
        loading: state.loading,
        nameToReplace: state.nameToReplace,
        category: state.category,
        searchCountryByName: (e) =>
          dispatch({ type: 'SEARCH_COUNTRY_BY_NAME', payload: e.target.value }),
        selectCountryByRegion: (e) =>
          dispatch({
            type: 'SELECT_COUNTRY_BY_REGION',
            payload: e.target.value,
          }),
        handleSubmit,
        jokesData,
      }}>
      {children}
    </Context.Provider>
  )
}
